document.addEventListener("DOMContentLoaded",(function(){document.getElementById("inputNamaDosen").value=sidangs[config.active].namaDosen,document.querySelector('input[name="jenis"][value="'+sidangs[config.active].jenis+'"]').checked=!0,document.querySelector('input[name="status"][value="'+sidangs[config.active].statusDosen+'"]').checked=!0,document.querySelector('input[name="jumlah"][value="'+sidangs[config.active].timMhs+'"]').checked=!0,document.getElementById("formSidangBaru").addEventListener("submit",(e=>{e.preventDefault();let n=sidangs[config.active];n.namaDosen=document.getElementById("inputNamaDosen").value,n.statusDosen=document.querySelector('input[name="status"]:checked').value,n.jenis=document.querySelector('input[name="jenis"]:checked').value,n.timMhs=document.querySelector('input[name="jumlah"]:checked').value,sidangs[config.active]=n,localStorage.setItem("sidangs",JSON.stringify(sidangs)),window.location.href="forms.html"}))}));