document.addEventListener("DOMContentLoaded",(function(){document.getElementById("inputNamaDosen").value=sidangs[config.active].namaDosen,document.querySelector('input[name="jenis"][value="'+sidangs[config.active].jenis+'"]').checked=!0,document.querySelector('input[name="status"][value="'+sidangs[config.active].statusDosen+'"]').checked=!0,document.querySelector('input[name="jumlah"][value="'+sidangs[config.active].timMhs+'"]').checked=!0,document.getElementById("formSidangBaru").addEventListener("submit",(e=>{e.preventDefault(),dataSidang.namaDosen=document.getElementById("inputNamaDosen").value,dataSidang.statusDosen=document.querySelector('input[name="status"]:checked').value,dataSidang.jenis=document.querySelector('input[name="jenis"]:checked').value,dataSidang.timMhs=document.querySelector('input[name="jumlah"]:checked').value,dataSidang.tanggal=(new Date).toISOString().split("T")[0],dataSidang.createdAt=(new Date).toISOString(),sidangs[config.active]=dataSidang,localStorage.setItem("sidangs",JSON.stringify(sidangs)),window.location.href="forms.html"}))}));